import React, { useState } from "react";
import { connect } from "react-redux";
import { getUsers } from "../../actions/users.actions/getUsers";
import { searchUserByUsername } from "../../actions/users.actions/searchUserByUsername";

const SearchUser = () => {
  let [searchValue, setSearchedUsername] = useState("");

  const onChange = (e) => setSearchedUsername(e.target.value);

  const searchForUser = () => {
    if (searchValue === "" || searchValue === null) getUsers();
    else searchUserByUsername(searchValue);
  };
  return (
    <header className="users-header">
      <p className="app_color_font font__bold font__p users-headline">Users</p>
      <br />

      <form className="search-user-wrapper">
        <textarea
          type="text"
          onChange={(e) => onChange(e)}
          value={searchValue}
        />

        <div
          className="user-search-button app_color_background font__p font__bold"
          onClick={() => searchForUser()}
        >
          Search for user
        </div>
      </form>
    </header>
  );
};

export default connect(null, { searchUserByUsername, getUsers })(SearchUser);
