import React, { useState } from "react";

const CreatePost = ({ createPost }) => {
  let [postText, setpostText] = useState("");

  const onChange = (e) => setpostText(e.target.value);

  const submitData = () => {
    if (postText !== "" && postText !== null) {
      createPost(postText);
    } else {
      alert("Text is empty!");
    }
    setpostText("");
  };
  return (
    <div className="tips-wrapper">
      <p className="font__p p__size font__bold app_color_font">
        <i className="fas fa-check-circle small_margin_right"></i>
        Tips on getting good answers quickly
      </p>
      <br />

      <ul className="tips">
        <li className="tip-item">
          <p className="font__p">
            <i className="fas fa-check small_margin_right"></i>
            Make sure your question hasn't been asked already
          </p>
        </li>

        <li className="tip-item">
          <p className="font__p">
            <i className="fas fa-check small_margin_right"></i>
            Keep your question short and to the point
          </p>
        </li>

        <li className="tip-item">
          <p className="font__p">
            <i className="fas fa-check small_margin_right"></i>
            Double-check grammar and spelling
          </p>
        </li>

        <li className="tip-item">
          <p className="font__p">
            <i className="fas fa-check small_margin_right"></i>
            Start Your question with "What","How","Why",etc.
          </p>
        </li>
      </ul>

      <form>
        <textarea
          type="text"
          value={postText}
          onChange={(e) => onChange(e)}
        />
        <div
          onClick={() => submitData()}
          className="app_color_background add-post-button font__p font__bold"
        >
          Add post
        </div>
      </form>
    </div>
  );
};

export default CreatePost;
